tinymce.PluginManager.add("orange_hr_bars",function(n,e){n.addButton("orange_hr",{title:"Orange Horizontal Line",cmd:"orange_hr",icon:"hr",classes:"orange-hr"}),n.addCommand("orange_hr",function(){n.execCommand("mceReplaceContent",!1,"<hr class='orange'>")}),n.addButton("orange_hr_wide",{title:"Wide Orange Horizontal Line",cmd:"orange_hr_wide",icon:"minus",classes:"orange-hr"}),n.addCommand("orange_hr_wide",function(){n.execCommand("mceReplaceContent",!1,"<hr class='orange wide'>")})});